	}
	
	public int leerDatoEntero(String mensaje) {
		int dato = 0;
		System.out.print(mensaje);
		dato = sc.nextInt();
		return dato;
	}
	
	public double leerDatoDecimal(String mensaje) {
		double dato = 0;
		System.out.print(mensaje);
		dato = sc.nextDouble();
		return dato;
